/* eslint-disable */
import { useState, useEffect, useContext } from 'react';
import {
  getPostById,
  getAllComments,
  addComment,
  deletePost,
  getLikeByUserIdAndPostId,
  addLikes,
  deleteLike,
  getLikeByPostId
} from '../../api';
import { useLocation, Redirect } from 'react-router-dom';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { Typography, TextField, Button } from '@material-ui/core';
import Avatar from '@material-ui/core/Avatar';
import { getDogPic } from '../../api';
import Divider from '@material-ui/core/Divider';
import { makeStyles } from '@material-ui/styles';
import { AuthContext } from '../../AuthContext';
import { ToastContainer, toast } from 'react-toastify';

import IconButton from '@material-ui/core/IconButton';
import ThumbUpAltOutlinedIcon from '@material-ui/icons/ThumbUpAltOutlined';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';

import 'react-toastify/dist/ReactToastify.css';
import { nullFormat } from 'numeral';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing(2),
    // textAlign: 'center',
    color: theme.palette.text.secondary
  }
}));

const Comment = ({ data }) => (
  <div style={{ fontSize: '40%' }}>
    <Divider style={{ margin: '15px' }} />
    <Grid container wrap="nowrap" spacing={2}>
      <Grid item>
        <Avatar alt="Remy Sharp" src={''} />
      </Grid>
      <Grid justifyContent="left" item xs zeroMinWidth>
        <h4 style={{ margin: 0, textAlign: 'left' }}>{data.user_id}l</h4>
        <p style={{ textAlign: 'left', color: 'gray' }}>{data.created_at}</p>
        <p style={{ textAlign: 'left' }}>{data.text}</p>
      </Grid>
    </Grid>
  </div>
);

const Post = (props) => {
  const [post, setPost] = useState({});
  const search = useLocation().search;
  const id = new URLSearchParams(search).get('id');
  const classes = useStyles();
  const [dogImg, setDogImg] = useState(
    'data:image/png;base64,'
  );

  const [comments, setComments] = useState([]);
  const [deleted, setDeleted] = useState(false);
  const [commentText, setCommentText] = useState('');
  const [liked, setLiked] = useState(null);
  const [likeCount, setLikeCount] = useState(0);

  const { isAuth, user } = useContext(AuthContext);

  useEffect(() => {
    getPostById(id).then((res) => {
      setPost(res.data);
      //console.log(res.data);
    });

    getDogPic().then((res) => {
      setDogImg(res.data.message);
    });

    getLikeByUserIdAndPostId(user.id, id).then((res) => {
      if (res.data === '') {
        setLiked(null);
      } else {
        setLiked(res.data.id);
      }
    });

    getLikeByPostId(id).then((res) => {
      // console.log(res.data);
      // console.log(res.data.length);
      setLikeCount(res.data.length);
    });
  }, []);

  const handleCommentSubmit = () => {
    if (commentText == '') {
      toast.info('Comment cannot be empty.', { position: 'top-center' });
    } else {
      addComment(id, user.id, commentText);
    }
  };

  const deleteForever = () => {
    deletePost(id).then(() => setDeleted(true));
  };

  const flipLike = () => {
    if (liked) {
      // console.log(liked);
      // console.log("liked");
      deleteLike(liked);
      setLiked(null);
      setLikeCount(likeCount - 1);
    } else {
      addLikes(user.id, id).then((res) => {
        let likeid = res.data;
        // console.log(res);
        if (likeid !== 'Like exists already') setLiked(likeid);
      });
      setLikeCount(likeCount + 1);
    }
  };

  useEffect(() => {
    getAllComments(id).then((res) => {
      setComments(res.data.reverse());
    });
  }, [handleCommentSubmit]);

  return (
    <>
      {deleted ? (
        <>
          <Redirect to="dashboard" />
        </>
      ) : (
        <>
          <ToastContainer />
          <div className={classes.root}>
            <Grid container spacing={3} style={{ margin: '10px' }}>
              <Grid item xs={3} />
              <Grid item xs={6}>
                <Paper className={classes.paper} elevation={3}>
                  {/* {post} */}
                  <Typography fontSize={34} sx={{ color: 'text.primary' }}>
                    {post.title}
                  </Typography>
                  <Typography>{new Date(post.created_at).toDateString()}</Typography>
                  <Typography>{post.user_id}</Typography>
                  <br />
                  <Typography>Name: {post.name}</Typography>
                  <Typography>Age: {post.age}</Typography>
                  <br />
                  <img alt="1" height="300px" src={dogImg} />
                  <br />
                  <Typography>{post.content}</Typography>
                </Paper>

                <div onClick={flipLike}>
                  <IconButton aria-label="like" style={{ float: 'left', width: '50%' }}>
                    <>
                      {liked ? (
                        <ThumbUpAltIcon fontSize="large" />
                      ) : (
                        <ThumbUpAltOutlinedIcon fontSize="large" />
                      )}
                      &nbsp;
                      {likeCount}
                    </>
                  </IconButton>
                </div>

                <div onClick={deleteForever}>
                  <IconButton aria-label="delete" style={{ float: 'right', width: '50%' }}>
                    <DeleteForeverIcon fontSize="large" />
                  </IconButton>
                </div>

                <br />

                <Paper
                  className={classes.paper}
                  elevation={3}
                  style={{ maxHeight: '400px', overflow: 'auto' }}
                >
                  {/* {post} */}
                  <Typography fontSize={34} sx={{ color: 'text.primary' }}>
                    Comments
                    {comments.map((comment, item) => (
                      <Comment data={comment} />
                    ))}
                  </Typography>
                </Paper>

                <br />
                {isAuth ? (
                  <Paper className={classes.paper} elevation={3}>
                    {/* {post} */}
                    <Grid container>
                      <TextField
                        style={{ width: '100%' }}
                        onChange={(e) => setCommentText(e.target.value)}
                      />
                    </Grid>
                    <Grid container>
                      <Button onClick={handleCommentSubmit}>Submit</Button>
                    </Grid>
                  </Paper>
                ) : (
                  <></>
                )}
                <br />
              </Grid>
              <Grid item xs={3} />
            </Grid>
          </div>
        </>
      )}
    </>
  );
};

export default Post;
